import React from "react";
import axios from "axios";
import ResultCard from "../components/ResultCard";
import "../styles.css";

const Home = () => {
  const API_KEY =
    "qt%2BHSez0GjoTYvzN9D49cDlbHcVmSSxlKAUmIrLEqtHhnJBf6URyeHVJYuTIP13Hm148dCo3U7qu0QeSF%2FXoZw%3D%3D";

  const LIFE = document.querySelector(".js-life");
  const CHAR = document.querySelector(".js-char");
  const INDVD = document.querySelector(".js-indvd");
  const DESIRE = document.querySelector(".js-desire");

  let LIFE_ARRAY = LIFE.value;
  let CHAR_ARRAY = CHAR.value;
  let INDVD_ARRAY = INDVD.value;
  let DESIRE_ARRAY = DESIRE.value;

  let resultList = "";

  function onChange() {
    LIFE_ARRAY = LIFE.value;
    CHAR_ARRAY = CHAR.value;
    INDVD_ARRAY = INDVD.value;
    DESIRE_ARRAY = DESIRE.value;
    console.log(LIFE_ARRAY, CHAR_ARRAY, INDVD_ARRAY, DESIRE_ARRAY);
  }

  function parseStr(dataSet) {
    const oParser = new DOMParser();
    const oDOM = oParser.parseFromString(dataSet, "text/xml");
    console.log(oDOM);
  }

  async function getData() {
    await axios({
      method: "get",
      url: `https://cors-anywhere.herokuapp.com/https://www.bokjiro.go.kr/openapi/rest/gvmtWelSvc?crtiKey=${API_KEY}&callTp=L&pageNum=1&numOfRows=100&lifeArray=${LIFE_ARRAY}&charTrgterArray=${CHAR_ARRAY}&trgterIndvdlArray=${INDVD_ARRAY}&desireArray=${DESIRE_ARRAY}`,
    }).then(function (response) {
      console.log(response);
      const dataSet = response.data;
      resultList = dataSet;
    });
    parseStr(resultList);
  }

  async function search() {
    await getData();
    console.log("done");
  }
  return (
    <div className="wrapper">
      <>
        <header>
          <h1>BBOKJIIIIII</h1>
          <h2>나만을 위한 복지검색</h2>
        </header>

        <section className="select-area">
          <ul>
            <li className="option-container">
              <h2 className="option-title">생애주기</h2>
              <select className="js-life" onChange={onChange}>
                <option value="none">선택하세요</option>
                <option value="001">영유아</option>
                <option value="002">아동</option>
                <option value="003">청소년</option>
                <option value="004">청년</option>
                <option value="005">중장년</option>
                <option value="006">노년</option>
              </select>
            </li>
            <li className="option-container">
              <h2 className="option-title">대상특성</h2>
              <select className="js-char" onChange={onChange}>
                <option value="none">선택하세요</option>
                <option value="001">해당없음</option>
                <option value="002">여성</option>
                <option value="003">임산부</option>
                <option value="004">장애</option>
                <option value="005">국가유동자등 보훈대상자</option>
                <option value="006">실업자</option>
              </select>
            </li>

            <li className="option-container">
              <h2 className="option-title">가구유형</h2>
              <select className="js-indvd" onChange={onChange}>
                <option value="none">선택하세요</option>
                <option value="001">해당없음</option>
                <option value="002">한부모</option>
                <option value="003">다문화</option>
                <option value="004">조손</option>
                <option value="005">새터민</option>
                <option value="006">소년소녀가장</option>
                <option value="007">독거노인</option>
              </select>
            </li>

            <li className="option-container">
              <h2 className="option-title">욕구</h2>
              <select className="js-desire" onChange={onChange}>
                <option value="none">선택하세요</option>
                <option value="0000000">안전</option>
                <option value="1000000">건강</option>
                <option value="2000000">일상생활유지</option>
                <option value="3000000">가족관계</option>
                <option value="4000000">사회적 관계</option>
                <option value="500000">경제</option>
                <option value="6000000">교육</option>
                <option value="7000000">고용</option>
                <option value="8000000">생활환경</option>
                <option value="9000000">법률 및 권익보장</option>
                <option value="A000000">기타</option>
              </select>
            </li>
          </ul>
        </section>

        <section className="search-container">
          <button onClick={search}>
            <h4>결과보기</h4>
          </button>
          <button onClick="clearAll()">
            <h4>초기화</h4>
          </button>
        </section>
        <section className="result-container">
          <div>
            <h2 className="js-result">살펴볼만한 복지를 가져왔습니다.</h2>
            <ResultCard />
          </div>
        </section>
      </>
    </div>
  );
};

export default Home;
