import React, { useState } from "react";
import axios from "axios";
import ResultCard from "../components/ResultCard";
import Selectors from "../components/Selector";
import "../styles.css";

const Home = () => {
  const API_KEY =
    "qt%2BHSez0GjoTYvzN9D49cDlbHcVmSSxlKAUmIrLEqtHhnJBf6URyeHVJYuTIP13Hm148dCo3U7qu0QeSF%2FXoZw%3D%3D";

  const LIFE = document.querySelector("#js-life");
  const CHAR = document.querySelector("#js-char");
  const INDVD = document.querySelector("#js-indvd");
  const DESIRE = document.querySelector("#js-desire");

  let LIFE_ARRAY = LIFE.value;
  let CHAR_ARRAY = CHAR.value;
  let INDVD_ARRAY = INDVD.value;
  let DESIRE_ARRAY = DESIRE.value;

  let resultList = "";

  function onChange() {
    LIFE_ARRAY = LIFE.value;
    CHAR_ARRAY = CHAR.value;
    INDVD_ARRAY = INDVD.value;
    DESIRE_ARRAY = DESIRE.value;
    console.log(LIFE_ARRAY, CHAR_ARRAY, INDVD_ARRAY, DESIRE_ARRAY);
  }

  function parseStr(dataSet) {
    const oParser = new DOMParser();
    const oDOM = oParser.parseFromString(dataSet, "text/xml");
    console.log(oDOM);
  }

  async function getData() {
    await axios({
      method: "get",
      url: `https://cors-anywhere.herokuapp.com/https://www.bokjiro.go.kr/openapi/rest/gvmtWelSvc?crtiKey=${API_KEY}&callTp=L&pageNum=1&numOfRows=100&lifeArray=${LIFE_ARRAY}&charTrgterArray=${CHAR_ARRAY}&trgterIndvdlArray=${INDVD_ARRAY}&desireArray=${DESIRE_ARRAY}`,
    }).then(function (response) {
      console.log(response);
      const dataSet = response.data;
      resultList = dataSet;
    });
    parseStr(resultList);
  }

  // var xmlToJson = convert.xml2json(resultList, { compact: true, spaces: 4 });

  // function makeList() {
  //   const
  // }

  async function search() {
    await getData();
    console.log("done");
  }
  return (
    <div className="wrapper">
      <>
        <header>
          <h1>BBOKJIIIIII</h1>
          <h2>나만을 위한 복지검색</h2>
        </header>

        <Selectors />

        <section className="search-container">
          <button onClick="getData()">
            <h4>결과보기</h4>
          </button>
          <button onClick="clearAll()">
            <h4>초기화</h4>
          </button>
        </section>
        <section className="result-container">
          <div>
            <h2 className="js-result">살펴볼만한 복지를 가져왔습니다.</h2>
            <ResultCard />
          </div>
        </section>
      </>
    </div>
  );
};

export default Home;
